changeSet.sql:
CREATE TABLE IF NOT EXISTS temp_feed_ids (
    id VARCHAR(36)
);

CREATE TABLE IF NOT EXISTS temp_user_ids (
    id VARCHAR(36)
);

-- Initialize row numbers SET @feed_rn := 0; SET @user_rn := 0;
-- Create indexed temp tables CREATE TEMPORARY TABLE indexed_feeds AS SELECT id AS feed_id, (@feed_rn := @feed_rn + 1) AS rn FROM temp_feed_ids;
CREATE TEMPORARY TABLE indexed_users AS SELECT id AS user_id, (@user_rn := @user_rn + 1) AS rn FROM temp_user_ids;
-- Get total number of users for round-robin assignment SELECT COUNT(*) INTO @total_users FROM indexed_users;
-- Insert feeds with round-robin assigned users INSERT INTO feed_item (id, user_id, reel_id, timestamp) SELECT r.feed_id, (SELECT user_id FROM indexed_users u WHERE u.rn = ((r.rn - 1) % @total_users) + 1), UUID(), -- generate random reel_id (or use fixed/test one) DATE_SUB(NOW(), INTERVAL FLOOR(RAND() * 60 * 24 * 30) MINUTE) AS timestamp FROM indexed_feeds r LIMIT 500000;
